import { useEffect, useState } from 'react'
import Styles from './TODO.module.css'
import { dummy } from './dummy'
import axios from 'axios';

export function TODO(props) {

    const [newTodo, setNewTodo] = useState('')
    const [todoData, setTodoData] = useState(dummy);
    const [loading, setLoading] = useState(true);
    const [editingId, setEditingId] = useState(null);
    const [editingValue, setEditingValue] = useState('');
    const [editingDescriptionId, setEditingDescriptionId] = useState(null);
    const [editingDescriptionValue, setEditingDescriptionValue] = useState('');

    useEffect(() => {
        const fetchTodo = async () => {
            const apiData = await getTodo()
            setTodoData(apiData);
            setLoading(false)
        }
        fetchTodo();
    }, [])

    const getTodo = async () => {
        const options = {
            method: "GET",
            url: `http://localhost:8000/api/todo`,
            headers: {
                accept: "application/json",
            }
        }
        try {
            const response = await axios.request(options)
            return response.data
        } catch (err) {
            console.log(err);
            return []; // return an empty array in case of error
        }
    }

    const addTodo = () => {
        const options = {
            method: "POST",
            url: `http://localhost:8000/api/todo`,
            headers: {
                accept: "application/json",
            },
            data: {
                title: newTodo,
                description: '' // Initialize with empty description
            }
        }
        axios
            .request(options)
            .then(function (response) {
                console.log(response.data)
                setTodoData(prevData => [...prevData, response.data.newTodo])
                setNewTodo(''); // Reset newTodo input
            })
            .catch((err) => {
                console.log(err)
            })
    }

    const deleteTodo = (id) => {
        const options = {
            method: "DELETE",
            url: `http://localhost:8000/api/todo/${id}`,
            headers: {
                accept: "application/json",
            }
        }
        axios
            .request(options)
            .then(function (response) {
                console.log(response.data)
                setTodoData(prevData => prevData.filter(todo => todo._id !== id))
            })
            .catch((err) => {
                console.log(err)
            })
    };

    const updateTodo = (id, updateData) => {
        const options = {
            method: "PATCH",
            url: `http://localhost:8000/api/todo/${id}`,
            headers: {
                accept: "application/json",
            },
            data: updateData
        }
        axios
            .request(options)
            .then(function (response) {
                console.log(response.data)
                setTodoData(prevData => prevData.map(todo => todo._id === id ? response.data : todo))
                setEditingId(null)
                setEditingValue('')
                setEditingDescriptionId(null)
                setEditingDescriptionValue('')
            })
            .catch((err) => {
                console.log(err)
            })
    };

    return (
        <div className={Styles.ancestorContainer}>
            <div className={Styles.headerContainer}>
                <h1>
                    Tasks
                </h1>
                <span>
                    <input
                        className={Styles.todoInput}
                        type='text'
                        name='New Todo'
                        value={newTodo}
                        onChange={(event) => {
                            setNewTodo(event.target.value)
                        }}
                    />
                    <button
                        id='addButton'
                        name='add'
                        className={Styles.addButton}
                        onClick={() => {
                            addTodo()
                            setNewTodo('')
                        }}
                    >
                        + New Todo
                    </button>
                </span>
            </div>
            <div id='todoContainer' className={Styles.todoContainer}>
                {loading ? (
                    <p style={{ color: 'white' }}>Loading...</p>
                ) : (
                    todoData.length > 0 ? (
                        todoData.map((entry, index) => (
                            <div key={entry._id} className={Styles.todo}>
                                <span className={Styles.infoContainer}>
                                    <input
                                        type='checkbox'
                                        checked={entry.done}
                                        onChange={() => {
                                            const options = {
                                                method: "PATCH",
                                                url: `http://localhost:8000/api/todo/${entry._id}`,
                                                headers: {
                                                    accept: "application/json",
                                                },
                                                data: {
                                                    ...entry,
                                                    done: !entry.done
                                                }
                                            }
                                            axios
                                                .request(options)
                                                .then(function (response) {
                                                    console.log(response.data)
                                                    setTodoData(prevData => prevData.map(todo => todo._id === entry._id ? response.data : todo))
                                                })
                                                .catch((err) => {
                                                    console.log(err)
                                                })
                                        }}
                                    />
                                    {editingId === entry._id ? (
                                        <input
                                            type="text"
                                            value={editingValue}
                                            className={Styles.editInput}
                                            onChange={(e) => setEditingValue(e.target.value)}
                                            onKeyDown={(e) => {
                                                if (e.key === 'Enter') {
                                                    updateTodo(entry._id, { title: editingValue });
                                                }
                                            }}
                                        />
                                    ) : (
                                        <div>
                                            <div>{entry.title}</div>
                                            {editingDescriptionId === entry._id ? (
                                                <textarea
                                                    value={editingDescriptionValue}
                                                    onChange={(e) => setEditingDescriptionValue(e.target.value)}
                                                    className={Styles.descriptionInput}
                                                    onKeyDown={(e) => {
                                                        if (e.key === 'Enter') {
                                                            updateTodo(entry._id, { description: editingDescriptionValue });
                                                        }
                                                    }}
                                                />
                                            ) : (
                                                <div>{entry.description}</div>
                                            )}
                                        </div>
                                    )}
                                </span>
                                <span
                                    style={{ cursor: 'pointer' }}
                                    onClick={() => {
                                        deleteTodo(entry._id);
                                    }}
                                >
                                    Delete
                                </span>
                                <span
                                    style={{ cursor: 'pointer' }}
                                    onClick={() => {
                                        if (editingId === entry._id) {
                                            setEditingId(null);
                                            setEditingValue('');
                                            setEditingDescriptionId(null);
                                            setEditingDescriptionValue('');
                                        } else {
                                            setEditingId(entry._id);
                                            setEditingValue(entry.title);
                                            setEditingDescriptionId(entry._id);
                                            setEditingDescriptionValue(entry.description);
                                        }
                                    }}
                                >
                                    {editingId === entry._id ? 'Cancel' : 'Edit'}
                                </span>
                            </div>
                        ))
                    ) : (
                        <p className={Styles.noTodoMessage}>No tasks available. Please add a new task.</p>
                    )
                )}
            </div>
        </div>
    )
}
